mapper.select:

select

from DEVICE
where SYSTEMID = #{systemid,jdbcType=BIGINT}
  and CTRLID = #{ctrlid,jdbcType=INTEGER}
  and DEVICEID = #{deviceid,jdbcType=INTEGER}
  and DEVICETYPE = #{devicetype,jdbcType=INTEGER}

mapper.delete:

delete from DEVICE
where SYSTEMID = #{systemid,jdbcType=BIGINT}
  and CTRLID = #{ctrlid,jdbcType=INTEGER}
  and DEVICEID = #{deviceid,jdbcType=INTEGER}
  and DEVICETYPE = #{devicetype,jdbcType=INTEGER}

mapper.update:

update DEVICE


    FARMLANDID = #{farmlandid,jdbcType=INTEGER},


    DEVICEDN = #{devicedn,jdbcType=VARCHAR},


    NAME = #{name,jdbcType=VARCHAR},


where SYSTEMID = #{systemid,jdbcType=BIGINT}
  and CTRLID = #{ctrlid,jdbcType=INTEGER}
  and DEVICEID = #{deviceid,jdbcType=INTEGER}
  and DEVICETYPE = #{devicetype,jdbcType=INTEGER}


update DEVICE
set FARMLANDID = #{farmlandid,jdbcType=INTEGER},
  DEVICEDN = #{devicedn,jdbcType=VARCHAR},
  NAME = #{name,jdbcType=VARCHAR}
where SYSTEMID = #{systemid,jdbcType=BIGINT}
  and CTRLID = #{ctrlid,jdbcType=INTEGER}
  and DEVICEID = #{deviceid,jdbcType=INTEGER}
  and DEVICETYPE = #{devicetype,jdbcType=INTEGER}

mapper.select:
