mapper.insert:

insert into ORDER (id, order_no, user_id, 
  payment, prepaid, province_name, 
  city_name, district_name, address, 
  create_time, company, weight
  )
values (#{id,jdbcType=INTEGER}, #{orderNo,jdbcType=BIGINT}, #{userId,jdbcType=INTEGER}, 
  #{payment,jdbcType=DECIMAL}, #{prepaid,jdbcType=DECIMAL}, #{provinceName,jdbcType=VARCHAR}, 
  #{cityName,jdbcType=VARCHAR}, #{districtName,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{company,jdbcType=VARCHAR}, #{weight,jdbcType=DECIMAL}
  )

mapper.select:
select district_name,COUNT(order_no) as orderCount from `ORDER`


    AND create_time>#{beginTime,jdbcType=TIMESTAMP}


    AND   create_time< #{endTime,jdbcType=TIMESTAMP} 


 GROUP BY district_name ORDER BY orderCount DESC limit 0,10;
