mapper.select:

SELECT sd.SALE_COUNT, sd.SALE_TIME, p1.NAME, p1.WEIGHT, p1.PRICE, p2.PROVINCENAME, c.CITYNAME,
d.DISTRICTNAME,sd.ADDRESS
FROM SALE_DETAIL sd, PRODUCTION p1, PROVINCE p2, CITY c, DISTRICT d
where sd.PRODUCT_ID = p1.PRODUCT_ID
and sd.PROVINCE_ID = p2.PROVINCEID
and sd.CITY_ID = c.ID
and sd.DISTRICT_ID = d.DISTRICTID
and sd.PROVINCE_ID = #{provinceId,jdbcType=INTEGER}
and sd.CITY_ID = #{cityId,jdbcType=INTEGER}
and sd.DISTRICT_ID = #{districtId,jdbcType=INTEGER}

mapper.insert:

insert into SALE_DETAIL (ID, USER_ID, PROVINCE_ID,
CITY_ID, DISTRICT_ID, PRODUCT_ID,
SALE_TIME, SALE_COUNT, ADDRESS
)
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{provinceId,jdbcType=INTEGER},
#{cityId,jdbcType=INTEGER}, #{districtId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER},
#{saleTime,jdbcType=TIMESTAMP}, #{saleCount,jdbcType=DECIMAL}, #{address,jdbcType=VARCHAR}
)
